import React, {useEffect, useState} from 'react'

import { ReactComponent as Remove } from '../assets/remove.svg'

const CartPage = () => {

    let [orders, setOrder] = useState([])

    useEffect(() => {
        getOrders()
        console.log(orders)
    }, [])

    let getOrders = async () => {
        let response = await fetch('http://127.0.0.1:8000/cart')
        let data = await response.json()
        setOrder(data);
    }

  return (
    <div className='container-fluid'>
        <h1>Order:</h1>
        <div className='row w-100 m-3 d-flex flex-column'>
          {orders.products?.map((product, index) => (
            <div key={index} className='col-11 d-flex flex-row justify-content-between align-items-center my-2 border border-dark'>
              <div className='col-8 col-md-6'>
                <div className='col-3 col-md-1'>
                  <img src={`http://127.0.0.1:8000/${product.product_image}`} className='img-fluid' />
                </div>
                <div>
                  <p className='fs-3 fw-bolder m-auto p-auto'>{product.product_name}</p>
                  <p className='fs-4'>${product.product_price}</p>
                </div>
              </div>
              <Remove />
            </div>
          ))}
        </div>
    </div>
  )
}

export default CartPage
