import React, {useState, useEffect} from 'react'

import { ReactComponent as Filter } from '../assets/filter.svg'

const Filters = () => {

    let [categories, setCategories] = useState([])

    useEffect(() => {
        getCategories();
    });

    let getCategories = async () => {
        let response = await fetch('http://127.0.0.1:8000/categories');
        let data = await response.json();
        setCategories(data);
    }

  return (
    <div>
        <div className='d-flex flex-row justify-content-center align-items-center'>
            <Filter />
            <h2 className='mx-1 mt-1'>Filters</h2>
        </div>
        <div>
            <h4>Categories:</h4>
            <div className='d-flex flex-row flex-wrap justify-content-evenly my-3'>
                {categories.map((category, index) => (
                    <div className='mb-3' key={index}>
                        <input type='checkbox' name={categories.category_name} value={category.category_name} className='mx-1' />
                        <label htmlFor={category.category_name}>{category.category_name}</label>
                    </div>
                ))}
            </div>
        </div>

        <div>
            <h4>Price:</h4>
            <div>
                <div>
                    <label htmlFor="from_price">From:</label>
                    <input type="number" name="from_price" className="form-control" />
                </div>
                <div>
                    <label htmlFor="to_price">To:</label>
                    <input type="number" name="to_price" className="form-control" />
                </div>
            </div>
        </div>

        <button className='w-100 btn btn-primary mt-3'>Filter</button>
    </div>
  )
}

export default Filters
