import React, {useState} from 'react'

const ProductItem = ({product}) => {

  let [cartProduct, setCartProduct] = useState([])

  let addToCart = async () => {
    let response = await fetch('http://127.0.0.1:8000/cart');
    let data = await response.json();
    setCartProduct(data);
  }

  return (
    <div className='col-11 col-md-5 col-lg-3 py-5 d-flex flex-column justify-content-center align-items-center product'>
      <div className='w-50'>
        <img src={`http://127.0.0.1:8000/${product.product_image}`} className='img-fluid' />
      </div>
        <h1>{product.product_name}</h1>
        <h2>${product.product_price}</h2>
        <button className='btn btn-primary px-5 py-2 mt-3'>&#128722; Add to Cart</button>
    </div>
  )
}

export default ProductItem
